// --- DIJKSTRA ALGORITHM ---
function dijkstraFare(start, end) {
    const dist = {};   // total fare
    const prev = {};   // previous node
    const Q = new Set(Object.keys(graph));
  
    // initialize
    for (let n in graph) dist[n] = Infinity;
    dist[start] = 0;
  
    while (Q.size) {
      // pick the cheapest node so far
      let u = [...Q].reduce((a, b) => dist[a] < dist[b] ? a : b);
  
      Q.delete(u);
  
      if (u === end) break;
  
      // explore neighbors
      for (let v in graph[u]) {
  
        // base fare for this segment
        let baseFare = graph[u][v].fare ?? 0;
  
        // transfer fare if changing mode
        let transferFare = 0;
  
        // previous step mode
        let prevNode = prev[u];
        if (prevNode) {
          let prevMode = graph[prevNode][u].mode;
          let currentMode = graph[u][v].mode;
  
          if (prevMode && currentMode && prevMode !== currentMode) {
            transferFare = graph[u][v].transfer ?? 0;
          }
        }
  
        const newFare = dist[u] + baseFare + transferFare;
  
        if (newFare < dist[v]) {
          dist[v] = newFare;
          prev[v] = u;
        }
      }
    }
  
    // reconstruct path
    const path = [];
    let node = end;
  
    while (node) {
      path.unshift(node);
      node = prev[node];
    }
  
    return { path, totalFare: dist[end] };
}  

