// --- ANIMATE JEEPNEY ALONG ROUTE ---
function animateJeepney(coords) {
  if (jeepMarker) {
    map.removeLayer(jeepMarker);
  }

  jeepMarker = L.marker(coords[0], { icon: jeepIcon }).addTo(map);

  let index = 0;

  function move() {
    if (index >= coords.length - 1) return;

    const start = coords[index];
    const end = coords[index + 1];
    let t = 0;
    const speed = 0.2; // adjust speed (lower = slower)

    function step() {
      t += speed;
      if (t > 1) {
        index++;
        move();
        return;
      }

      const lat = start[0] + (end[0] - start[0]) * t;
      const lng = start[1] + (end[1] - start[1]) * t;

      jeepMarker.setLatLng([lat, lng]);
      requestAnimationFrame(step);
    }
    step();
  }
  move();
}

